/*     File: Book.m */#import "Book.h"@implementation Book@synthesize title, author, copyright;-(void)setTitle:(NSString *)titleString{    if (![titleString isEqualToString:title]) {        [title release];        title = [titleString copy];    }                }- (void)dealloc {	[title release];	[author release];	[copyright release];	[super dealloc];	}@end