/*     File: SimpleUndoAppDelegate.m Abstract: Application delegate to configure the view and navigation controllers.  Version: 1.0  */#import "SimpleUndoAppDelegate.h"#import "RootViewController.h"#import "Book.h"@implementation SimpleUndoAppDelegate@synthesize window;@synthesize navigationController;#pragma mark -#pragma mark Application lifecycle- (void)applicationDidFinishLaunching:(UIApplication *)application {        	// Tell the application to support shake-to-edit.	application.applicationSupportsShakeToEdit = YES;		// Create a new book for the root controller.	RootViewController *rootViewController = (RootViewController *)[navigationController topViewController];		Book *newBook = [[[Book alloc] init]autorelease];	rootViewController.book = newBook;		// Configure and display the window.	[window addSubview:[navigationController view]];    [window makeKeyAndVisible];}#pragma mark -#pragma mark Memory management- (void)dealloc {	[navigationController release];	[window release];	[super dealloc];}@end